import { execSync } from "node:child_process";
import * as process from "node:process";
/** Run the cmd synchronously. Output goes to terminal. */
export const shell = (cmd, { env, ...otherOptions } = {}) => {
    execSync(cmd, {
        env: { ...process.env, ...env },
        ...otherOptions,
        stdio: "inherit"
    });
};
/** Run the cmd synchronously, returning output as a string */
export const getShellOutput = (cmd, { env, ...otherOptions } = {}) => execSync(cmd, {
    env: { ...process.env, ...env },
    ...otherOptions,
    stdio: "pipe"
}).toString();
